const axios = require("axios");

exports.handleReply = (setReplyComment) => {
  setReplyComment(true);
};

exports.handleReplyActive = async (setReplyComment, text, comment) => {
  console.log("text:", text);
  console.log("comment:", comment);
  try {
    await axios.put(
      `http://localhost:8000/api/comments/reply/${comment.user._id}`,
      {
        content: `@${comment.user.username} ${text}`,
        type: "reply",
        user: "6258efc327f17aca0c4c44eb",
      }
    );
    setReplyComment(false);
    window.location.reload(true);
  } catch (err) {
    console.log(err.message);
  }
};
