import React, { useEffect } from "react";

export const DeleteModal = React.forwardRef((props, deleteRef) => {
  // useEffect(() => {
  //   if (deleteRef.current.style.display === "block") {
  //     window.onclick = (e) => {
  //       if (e.target === deleteRef.current) {
  //         deleteRef.current.style.display = "none";
  //       }
  //     };
  //   }
  // }, [deleteRef.current.style.display]);
  return (
    <div ref={deleteRef} className="container-modal">
      <div className="outer-modal">
        <div className="inner-modal">
          <div className="title-modal">
            <h1>Delete comment</h1>
          </div>
          <div className="body-modal">
            Are you sure you want to delete this comment? This will remove the
            comment and can't be undone.
          </div>
          <div className="btns-modal">
            <button className="cancel-btn">NO,CANCEL</button>
            <button className="delete-btn">YES,DELETE</button>
          </div>
        </div>
      </div>
    </div>
  );
});
