import { useState, useRef } from "react";
import Scores from "./Scores";
import Info from "./Info";
import Reply from "./Reply";
import Delete from "./Delete";
import Update from "./Update";
import Edit from "./Edit";
import NewComment from "./NewComment";
import {
  handleReply,
  handleReplyActive,
  handleEditComment,
  handleUpdateComment,
} from "../utils";

export default function Replies({ reply, currentUser, commentUser }) {
  const [text, setText] = useState(reply.content);
  const [replyComment, setReplyComment] = useState(false);
  const [update, setUpdate] = useState(false);

  const textRef = useRef();

  const handleText = (e) => {
    setText(e.target.value);
  };

  return (
    <div>
      <div className="comment ">
        <div className="comment-scores">
          <Scores
            score={reply.score}
            currentUser={currentUser}
            commentId={reply._id}
          />
        </div>
        <div className="comment-info">
          <div className="comment-controllers">
            {currentUser.username === reply.user.username ? (
              <div className="info">
                <Info
                  profileUser="you"
                  user={reply.user}
                  createdAt={reply.createdAt}
                />
              </div>
            ) : (
              <div className="info">
                <Info
                  profileUser="user"
                  user={reply.user}
                  createdAt={reply.createdAt}
                />
              </div>
            )}
            {currentUser.username === reply.user.username ? (
              <div className="comment-delete">
                <Delete />
                <Edit
                  handleEditComment={() => {
                    handleEditComment(textRef, setUpdate);
                  }}
                />
              </div>
            ) : (
              <div className="comment-reply">
                <Reply handleReply={() => handleReply(setReplyComment)} />
              </div>
            )}
          </div>
          <div className="text text-reply">
            <textarea
              name="comment"
              value={`@${commentUser}`}
              {...text}
              readOnly={false}
              onChange={handleText}
              ref={textRef}
            />
            <div className="invisible">
              {`@${commentUser}`}
              {text}
            </div>
          </div>
          {update && (
            <div className="comment-update">
              <Update
                handleUpdateComment={() =>
                  handleUpdateComment(reply, currentUser, text)
                }
              />
            </div>
          )}
        </div>
      </div>
      {replyComment && (
        <NewComment
          currentUser={currentUser}
          reply={true}
          handleReplyActive={(txt) =>
            handleReplyActive(setReplyComment, txt, reply, currentUser)
          }
        />
      )}
    </div>
  );
}
