import { useState, useRef } from "react";
import Delete from "./Delete";
import Edit from "./Edit";
import Info from "./Info";
import Replies from "./Replies";
import Reply from "./Reply";
import Scores from "./Scores";
import NewComment from "./NewComment";
import Update from "./Update";
import { handleReply, handleReplyActive, handleEditComment } from "../utils";

export default function Comment({ comment, currentUser }) {
  const [text, setText] = useState(comment.content);
  const [replyComment, setReplyComment] = useState(false);
  const [update, setUpdate] = useState(false);

  const textRef = useRef();

  const handleText = (e) => {
    setText(e.target.value);
  };

  const handleReadOnly = () => {
    textRef.current.readOnly = false;
    setUpdate(true);
    textRef.current.parentNode.classList.add("text-edit");
  };
  return (
    <>
      <div>
        <div className="comment">
          <div className="comment-scores">
            <Scores
              score={comment.score}
              currentUser={currentUser}
              commentId={comment._id}
            />
          </div>
          <div className="comment-info">
            <div className="comment-controllers">
              {currentUser.username === comment.user.username ? (
                <div className="info">
                  <Info
                    profileUser="you"
                    user={comment.user}
                    createdAt={comment.createdAt}
                  />
                </div>
              ) : (
                <div className="info">
                  <Info
                    profileUser="user"
                    user={comment.user}
                    createdAt={comment.createdAt}
                  />
                </div>
              )}

              {currentUser.username === comment.user.username ? (
                <div className="comment-delete">
                  <Delete />
                  <Edit
                    handleEditComment={() => {
                      handleEditComment(handleReadOnly);
                    }}
                  />
                </div>
              ) : (
                <div className="comment-reply">
                  <Reply handleReply={() => handleReply(setReplyComment)} />
                </div>
              )}
            </div>
            <div className="text">
              <textarea
                name="comment"
                value={text}
                readOnly={true}
                autoFocus={true}
                onChange={handleText}
                ref={textRef}
              />
              <div className="invisible">{text}</div>
            </div>
            {update && (
              <div className="comment-update">
                <Update />
              </div>
            )}
          </div>
        </div>
        {replyComment && (
          <NewComment
            currentUser={currentUser}
            handleReplyActive={(txt) => {
              handleReplyActive(setReplyComment, txt, comment, currentUser);
            }}
            reply
          />
        )}
        <div className="comment-replies">
          {comment.replies.map((reply) => (
            <Replies
              currentUser={currentUser}
              commentUser={comment.user.username}
              reply={reply}
              key={reply.id || reply._id}
              handleReplyActive={(txt) => {
                handleReplyActive(setReplyComment, txt);
              }}
            />
          ))}
        </div>
      </div>
    </>
  );
}
